mapper.select:
/*order.selectOrder_User*/
SELECT DISTINCT om_no , o_no ,(select DISTINCT m_name from m_user m, order_management om1 where m.m_id = om.request_id and om.om_no = om1.OM_NO) as request_name ,(select DISTINCT m_name from m_user m, order_management om2 where m.m_id = om.response_id and om.om_no = om2.OM_NO) as response_name ,p_name ,p_count ,om_date
FROM order_management om, m_user m wHERE RESPONSE_ID = #{m_id ,jdbcType=VARCHAR} and om_date like to_char(sysdate, 'YY/MM/DD%') order by om_date desc

/*order.ResponseDate_User*/
SELECT DISTINCT om_no , o_no ,(select DISTINCT m_name from m_user m, order_management om1 where m.m_id = om.request_id and om.om_no = om1.OM_NO) as request_name ,(select DISTINCT m_name from m_user m, order_management om2 where m.m_id = om.response_id and om.om_no = om2.OM_NO) as response_name ,p_name ,p_count ,om_date
FROM order_management om, m_user m wHERE RESPONSE_ID = #{m_id ,jdbcType=VARCHAR} and substr(om_date, 0, 8) BETWEEN to_date( #{startDate}, 'YY/MM/DD') and to_date( #{endDate}, 'YY/MM/DD') order by om_date desc
